package guru.springframework.spring6webapp.bootstrap;import guru.springframework.spring6webapp.domain.Author;import guru.springframework.spring6webapp.domain.Book;import guru.springframework.spring6webapp.domain.Publisher;import guru.springframework.spring6webapp.repositories.AuthorRepository;import guru.springframework.spring6webapp.repositories.BookRepository;import guru.springframework.spring6webapp.repositories.PublisherRepository;import org.springframework.boot.CommandLineRunner;import org.springframework.stereotype.Component;@Componentpublic class BootstrapData implements CommandLineRunner {    private final AuthorRepository authorRepository;    private final BookRepository bookRepository;    private final PublisherRepository publisherRepository;    public BootstrapData(AuthorRepository authorRepository, BookRepository bookRepository, PublisherRepository publisherRepository) {        this.authorRepository = authorRepository;        this.bookRepository = bookRepository;        this.publisherRepository = publisherRepository;    }    @Override    public void run(String... args) throws Exception {        Publisher hawk = new Publisher();        hawk.setPublisherName("Hawk Publishing");        hawk.setAddress("12345 fake street");        hawk.setCity("Vancouver");        hawk.setProvince("BC");        hawk.setPostalCode("90210");        Author rob = new Author();        rob.setFirstName("Rob");        rob.setLastName("Gleason");        Book rpg = new Book();        rpg.setTitle("Role Playing Games");        rpg.setIsbn("123456");        Author robSaved = authorRepository.save(rob);        Book rpgSaved = bookRepository.save(rpg);        Publisher hawkSaved = publisherRepository.save(hawk);        Author eric = new Author();        eric.setFirstName("Eric");        eric.setLastName("Evans");        Book ddd = new Book();        ddd.setTitle("Domain Driven Design");        ddd.setIsbn("789101112");        Author ericSaved = authorRepository.save(eric);        Book dddSaved = bookRepository.save(ddd);        rpgSaved.setPublisher(hawkSaved);        dddSaved.setPublisher(hawkSaved);        robSaved.getBooks().add(rpgSaved);        ericSaved.getBooks().add(dddSaved);        rpgSaved.getAuthors().add(robSaved);        dddSaved.getAuthors().add(ericSaved);        authorRepository.save(robSaved);        authorRepository.save(ericSaved);        bookRepository.save(rpgSaved);        bookRepository.save(dddSaved);        publisherRepository.save(hawkSaved);        System.out.println("In Bootstrap");        System.out.println("Author Count: " + authorRepository.count());        System.out.println("Book Count: " + bookRepository.count());        System.out.println("Publisher Count " + publisherRepository.count());    }}