package guru.springframework.spring6webapp.bootstrap;import guru.springframework.spring6webapp.domain.Author;import guru.springframework.spring6webapp.domain.Book;import guru.springframework.spring6webapp.repositories.AuthorRepository;import guru.springframework.spring6webapp.repositories.BookRepository;import org.springframework.boot.CommandLineRunner;import org.springframework.stereotype.Component;@Componentpublic class BootstrapData implements CommandLineRunner {    private final AuthorRepository authorRepository;    private final BookRepository bookRepository;    public BootstrapData(AuthorRepository authorRepository, BookRepository bookRepository) {        this.authorRepository = authorRepository;        this.bookRepository = bookRepository;    }    @Override    public void run(String... args) throws Exception {        Author rob = new Author();        rob.setFirstName("Rob");        rob.setLastName("Gleason");        Book rpg = new Book();        rpg.setTitle("Role Playing Games");        rpg.setIsbn("123456");        Author robSaved = authorRepository.save(rob);        Book rpgSaved = bookRepository.save(rpg);        Author eric = new Author();        eric.setFirstName("Eric");        eric.setLastName("Evans");        Book ddd = new Book();        ddd.setTitle("Domain Driven Design");        ddd.setIsbn("789101112");        Author ericSaved = authorRepository.save(eric);        Book dddSaved = bookRepository.save(ddd);        robSaved.getBooks().add(rpgSaved);        ericSaved.getBooks().add(dddSaved);        authorRepository.save(robSaved);        authorRepository.save(ericSaved);        System.out.println("In Bootstrap");        System.out.println("Author Count: " + authorRepository.count());        System.out.println("Book Count: " + bookRepository.count());    }}